import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import onError from '../utils/onError';
import SockJS from 'sockjs-client';
import axios from 'axios';
import getAvatarColor from '../utils/getAvatarColor';

let stompClient;

const ChatRoom = () => {
  const [privateChats, setPrivateChats] = useState(new Map());
  const [publicChats, setPublicChats] = useState([]);
  const [tab, setTab] = useState("CHATROOM");
  const [friendInput, setFriendInput] = useState({
    friendInput: ""
  });
  const [addFriendTabOpen, setAddFriendTabOpen] = useState(false);
  const [userData, setUserData] = useState({
    username: '',
    receivername: '',
    connected: false,
    message: ''
  });

  useEffect(() => {
    console.log(userData);
  }, [userData]);

  const connectWebSocket = () => {
    const socketURL = 'http://localhost:8080/ws';
    const socket = new SockJS(socketURL);
    stompClient = over(socket);
    stompClient.connect({}, onConnected, onError);
  };

  const onConnected = () => {
    setUserData({ ...userData, "connected": true });
    stompClient.subscribe('/chatroom/public', onMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
    userJoin();
  };

  const userJoin = () => {
    let chatMessage = {
      senderName: userData.username,
      status: "JOIN"
    };
    stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
  };

  const onMessageReceived = (payload) => {
    let payloadData = JSON.parse(payload.body);
    switch (payloadData.status) {
      case "JOIN":    
        const updatedPrivateChats = new Map(privateChats);
        if (!updatedPrivateChats.has(payloadData.senderName)) {
            let existingMessages = updatedPrivateChats.get(payloadData.senderName);
            console.log("HERE", privateChats);
            let updatedMessages = existingMessages.concat(payloadData);
            updatedPrivateChats.set(payloadData.senderName, updatedMessages);
            setPrivateChats(updatedPrivateChats);
          } else {
            updatedPrivateChats.set(payloadData.senderName, [payloadData]);
          }
        break;
      case "MESSAGE":
        setPublicChats((prevChats) => [...prevChats, payloadData]);
        break;
    }
  };

  const onPrivateMessage = (payload) => {
    let payloadData = JSON.parse(payload.body);
    setPrivateChats((prevPrivateChats) => {
      const updatedPrivateChats = new Map(prevPrivateChats);
      if (updatedPrivateChats.has(payloadData.senderName)) {
        let existingMessages = updatedPrivateChats.get(payloadData.senderName);
        let updatedMessages = existingMessages.concat(payloadData);
        updatedPrivateChats.set(payloadData.senderName, updatedMessages);
      } else {
        updatedPrivateChats.set(payloadData.senderName, [payloadData]);
      }
      return updatedPrivateChats;
    });
  };

  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, "message": value });
  };

  const sendValue = () => {
    if (stompClient) {
      let chatMessage = {
        senderName: userData.username,
        message: userData.message,
        status: "MESSAGE"
      };
      console.log(chatMessage);
      stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, "message": "" });
    }
  };

  const sendPrivateValue = () => {
    if (stompClient) {
      let chatMessage = {
        senderName: userData.username,
        receiverName: tab,
        message: userData.message,
        status: "MESSAGE"
      };

      if (userData.username !== tab) {
        privateChats.get(tab).push(chatMessage);
        setPrivateChats(new Map(privateChats));
      }
      stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, "message": "" });
    }
  };

  const handleUsername = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, "username": value });
  };

  const handleFriendInput = (event) => {
    const { value } = event.target;
    setFriendInput({ ...friendInput, });
  };

  const transformArrayToMap = (array) => {
    const map = new Map();
    array.forEach((item) => {
      const key = item.senderName !== userData.username ? item.senderName : item.chatId;
      const value = {
        id: item.id,
        senderName: item.senderName,
        recieverName: item.chatId,
        message: item.message,
        status: "MESSAGE",
      };

      if (!map.has(key)) {
        map.set(key, []);
      }

      map.get(key).push(value);
    });

    return map;
  };

  const registerUser = async () => {
    try {
      const config = {
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
        }
      };
      const registerResponse = await axios.post("http://localhost:8080/chat-user-register", { name: userData.username }, config);
      const publicResponse = await axios.get("http://localhost:8080/messages/public", config);
      const privateResponse = await axios.get("http://localhost:8080/messages/private/" + userData.username, config);
      const publicChatData = publicResponse.data.map((publicMessage) => ({ ...publicMessage, status: "MESSAGE", receiverName: null }));
      const privateChatData = privateResponse.data;

      const MapPrivateChat = transformArrayToMap(privateChatData);
      setPublicChats(publicChatData);
      setPrivateChats(MapPrivateChat);
      connectWebSocket();
    } catch (error) {
      console.log(error.message);
    }
  };

  const searchForFriend = async () => {
    try {
      const config = {
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
        }
      };
      const friendResponse = await axios.get("http://localhost:8080/chat-user-register/user/as" + userData.username, config);
      const friendData = friendResponse.data;
      if(friendData) {
        console.log(friendData);
      } else {
        console.log("Esse usuário não existe")
      }
    } catch (error) {
      console.log(error.message);
    }
  }
  return (
    <div>
      {userData.connected ? (
        <div className="chat-box">
          <div className="member-list">
            <ul>
              <li onClick={() => {
                setTab("CHATROOM");
                setAddFriendTabOpen(false);
                }} className={`member ${(tab === "CHATROOM" &&  !addFriendTabOpen) && "active"}`}>Chatroom</li>
              {[...privateChats.keys()].map((name, index) => (
                <li onClick={() => {
                  setTab(name);
                  setAddFriendTabOpen(false);
                }} className={`member ${(tab === name &&  !addFriendTabOpen) && "active"}`} key={index}>{name}</li>
              ))}
              <li className={`member  ${(addFriendTabOpen) && "active"}`} onClick={() => setAddFriendTabOpen(true)}>Adicionar amigo + </li>
            </ul>
          </div>
          {tab === "CHATROOM" && !addFriendTabOpen && (
            <div className="chat-content">
              <ul className="chat-messages">
                {publicChats.map((chat, index) => (
                  <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                    {chat.senderName !== userData.username && (
                      <div
                        className="avatar"
                        style={{ backgroundColor: getAvatarColor(chat.senderName) }}
                      >
                        {chat.senderName}
                      </div>
                    )}
                    <div className="message-data">{chat.message}</div>
                    {chat.senderName === userData.username && (
                      <div className="avatar self">{chat.senderName}</div>
                    )}
                  </li>
                ))}
              </ul>

              <div className="send-message">
                <input
                  type="text"
                  className="input-message"
                  placeholder="enter the messageenter the message"
                  value={userData.message}
                  onChange={handleMessage}
                />
                <button type="button" className="send-button" onClick={sendValue}>
                  send
                </button>
              </div>
            </div>
          )}
          {tab !== "CHATROOM" && !addFriendTabOpen&&(
            <div className="chat-content">
              <ul className="chat-messages">
                {[...privateChats.get(tab)].map((chat, index) => (
                  <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                    {chat.senderName !== userData.username && (
                      <div
                        className="avatar"
                        style={{ backgroundColor: getAvatarColor(chat.senderName) }}
                      >
                        {chat.senderName}
                      </div>
                    )}
                    <div className="message-data">{chat.message}</div>
                    {chat.senderName === userData.username && (
                      <div className="avatar self">{chat.senderName}</div>
                    )}
                  </li>
                ))}
              </ul>

              <div className="send-message">
                <input
                  type="text"
                  className="input-message"
                  placeholder="enter the message"
                  value={userData.message}
                  onChange={handleMessage}
                />
                <button type="button" className="send-button" onClick={sendPrivateValue}>
                  send
                </button>
              </div>
            </div>
          )}
          {
            addFriendTabOpen && (
                  <div className="chat-content">
                    <div className="chat-messages">
                      <div>
                        <input type="text" name="friendName" />
                        <button onChange={handleFriendInput} onClick={searchForFriend}>
                          Search
                        </button>
                      </div>
                      <div>
                      </div> 
                    </div>
                  </div>
                )
              }
        </div>
      ) : (
        <div className="register">
          <input
            id="user-name"
            placeholder="Enter your name"
            name="userName"
            value={userData.username}
            onChange={handleUsername}
            margin="normal"
          />
          <button type="button" onClick={registerUser}>
            connect
          </button>
        </div>
      )}
    </div>
  );
};

export default ChatRoom;
