import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import onError from '../utils/onError';
import SockJS from 'sockjs-client';
import searchForFriend from '../utils/searchForFriend';
import scrollToBottom from '../utils/scrollToBottom';
import getAvatarColor from '../utils/getAvatarColor';
import RegisterForm from './RegisterForm';
import addFriend from '../utils/addFriend';
import handleMessage from '../utils/handleMessage';
import sendPrivateValue from '../utils/sendPrivateValue';
import handleChange from '../utils/handleFriendInput';
import api_data from '../api/config';
import sendValue from '../utils/sendValue';

let stompClient;

const ChatRoom = () => {
  const [privateChats, setPrivateChats] = useState(new Map());
  const [publicChats, setPublicChats] = useState([]);
  const [tab, setTab] = useState("CHATROOM");
  const [friendInput, setFriendInput] = useState({
    friendName: "",
  });
  const [addFriendTabOpen, setAddFriendTabOpen] = useState(false);
  const [userData, setUserData] = useState({
    username: '',
    receivername: '',
    connected: false,
    message: ''
  });

  useEffect(() => {
    scrollToBottom();
  }, [publicChats, privateChats, tab]);

  useEffect(() => {
    scrollToBottom();
  }, [userData]);

  const connectWebSocket = () => {
    const { URL, PORT } = api_data;
    const socketURL = `http://${URL}:${PORT}/ws`;
    const socket = new SockJS(socketURL);
    stompClient = over(socket);
    stompClient.connect({}, onConnected, onError);
  };

  const onConnected = () => {
    setUserData({ ...userData, "connected": true });
    stompClient.subscribe('/chatroom/public', onMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
    userJoin();
  };

  const userJoin = () => {
    let chatMessage = {
      senderName: userData.username,
      status: "JOIN"
    };
    stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
  };

  const onMessageReceived = (payload) => {
    let payloadData = JSON.parse(payload.body);
    switch (payloadData.status) {
      case "JOIN":    
        const updatedPrivateChats = new Map(privateChats);

        if (payloadData.senderName !== userData.username) {
          setPrivateChats((prevChats) => {
            const prevPrivateChats = new Map(prevChats);
            const existingMessages = prevPrivateChats.get(payloadData.senderName) || [];
            prevPrivateChats.set(payloadData.senderName, existingMessages);
            return prevPrivateChats;
          });
        } else {
          updatedPrivateChats.set(payloadData.senderName, [payloadData]);
        }
        break;        
      case "MESSAGE":
        setPublicChats((prevChats) => [...prevChats, payloadData]);
        break;
    }
  };


  const onPrivateMessage = (payload) => {
    let payloadData = JSON.parse(payload.body);
    setPrivateChats((prevPrivateChats) => {
      const updatedPrivateChats = new Map(prevPrivateChats);
      if (updatedPrivateChats.has(payloadData.senderName)) {
        let existingMessages = updatedPrivateChats.get(payloadData.senderName);
        let updatedMessages = existingMessages.concat(payloadData);
        updatedPrivateChats.set(payloadData.senderName, updatedMessages);
      } else {
        updatedPrivateChats.set(payloadData.senderName, [payloadData]);
      }
      return updatedPrivateChats;
    });
  };

  const handleMessage = (event, userData) => {
    const { value } = event.target;
    setUserData({ ...userData, "message": value });
  };

  return (
    <div>
      {userData.connected ? (
        <div className="chat-box">
          <div className="member-list">
            <ul>
              <li onClick={() => {
                setTab("CHATROOM");
                setAddFriendTabOpen(false);
                scrollToBottom();
                }} className={`member ${(tab === "CHATROOM" &&  !addFriendTabOpen) && "active"}`}>Chatroom</li>
              {[...privateChats.keys()].map((name, index) => (
                <li onClick={() => {
                  setTab(name);
                  setAddFriendTabOpen(false);
                  scrollToBottom();
                }} className={`member ${(tab === name &&  !addFriendTabOpen) && "active"}`} key={index}>{name}</li>
              ))}
              <li className={`member  ${(addFriendTabOpen) && "active"}`} onClick={() => setAddFriendTabOpen(true)}>Adicionar amigo + </li>
            </ul>
          </div>
          {tab === "CHATROOM" && !addFriendTabOpen && (
            <div className="chat-content">
              <ul className="chat-messages">
                {publicChats.map((chat, index) => (
                  <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                    {chat.senderName !== userData.username && (
                      <div
                        className="avatar"
                        style={{ backgroundColor: getAvatarColor(chat.senderName) }}
                      >
                        {chat.senderName}
                      </div>
                    )}
                    <div className="message-data">{chat.message}</div>
                    {chat.senderName === userData.username && (
                      <div className="avatar self">{chat.senderName}</div>
                    )}
                  </li>
                ))}
              </ul>

              <div className="send-message">
                <input
                  type="text"
                  className="input-message"
                  placeholder="enter the messageenter the message"
                  value={userData.message}
                  onChange={(event) => handleMessage(event, userData, setUserData)}
                />
                <button type="button" className="send-button" onClick={() => sendValue(stompClient, userData, setUserData)}>
                  send
                </button>
              </div>
            </div>
          )}
          {tab !== "CHATROOM" && !addFriendTabOpen&&(
            <div className="chat-content">
              <ul className="chat-messages">
                {[...privateChats.get(tab)].map((chat, index) => (
                  <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                    {chat.senderName !== userData.username && (
                      <div
                        className="avatar"
                        style={{ backgroundColor: getAvatarColor(chat.senderName) }}
                      >
                        {chat.senderName}
                      </div>
                    )}
                    <div className="message-data">{chat.message}</div>
                    {chat.senderName === userData.username && (
                      <div className="avatar self">{chat.senderName}</div>
                    )}
                  </li>
                ))}
              </ul>

              <div className="send-message">
                <input
                  type="text"
                  className="input-message"
                  placeholder="enter the message"
                  value={userData.message}
                  onChange={(event) => handleMessage(event, userData, setUserData)}
                />
                <button type="button" className="send-button" onClick={() => sendPrivateValue(stompClient, userData, tab, setPrivateChats, setUserData)}>
                  send
                </button>
              </div>
            </div>
          )}
          {
            addFriendTabOpen && (
                  <div className="chat-content">
                    <div className="chat-messages search-div">
                      <div className="search-container">
                        <input type="text" name="friendName" value={friendInput.friendName} onChange={(event) => handleChange(event, setFriendInput, friendInput)} />
                        <button onClick={() => searchForFriend(friendInput, setFriendInput)}>
                          Search
                        </button>
                      </div>
                      <div>
                        { (!friendInput.friendNotFound && friendInput.friendNotFound !== undefined) ? (
                          <div className="add-user-container">
                            <span>{friendInput.name}</span>
                            <button onClick={() => addFriend(setPrivateChats, setFriendInput, friendInput)}>Add +</button>
                          </div>
                        ) : (
                          <div>
                            {friendInput.friendNotFound !== undefined && <span>Friend not found!</span>}
                          </div>
                        )}
                      </div> 
                    </div>
                  </div>
                )
              }
        </div>
      ) : (
        <RegisterForm
          setUserData={setUserData}
          userData={userData}
          connectWebSocket={connectWebSocket}
          setPublicChats={setPublicChats}
          setPrivateChats={setPrivateChats}
          />
      )}
    </div>  
  );
};

export default ChatRoom;
